import React, { useEffect, useState } from 'react'
import { FlatList, Text, StyleSheet, Image, View, Alert, TouchableOpacity } from 'react-native';
import Weatherapi from './WeatherApi';

export function List() {
    const [countries, setCountries] = useState([])
    const [weatherCapital, setWeatherCapital] = useState([])
    const [dataCountry, setDataCountry] = useState(false)
    const [countrySelected, setCountrySelected] = useState("")


    useEffect(()=>{

        const getCountries = async() =>{
            const res = await fetch("http://restcountries.eu/rest/v2/all");
            const data = await res.json();
            setCountries(data)

            
        }
        getCountries();
        
    },[])
    

    const capitalWeather = async(e) => {
        e.preventDefault();
        if (countries.name === countrySelected){
            const capitalInformation = await Weatherapi(countries.capital);
            if (capitalInformation.message) {
                alert("sorry, retry a bit later !")
            } else {
                setWeatherCapital(capitalInformation)
            }
        }
    }

    const handlePress = (capital) =>{
        setCountrySelected(capital)
        capitalWeather()
        console.log("you hit me !")
        Alert.alert("you hit me !")
        setDataCountry(!dataCountry)
    };

    const renderCountrie = ({item}) =>{ 
        return (
        <View style={styles.dataCountrie}>
            <Text>{item.name}</Text>
            <View style={styles.dataCapital}>
                {dataCountry && <>
                    <Text style={styles.text}>{item.capital}</Text>
                    <Text style={styles.text}>-</Text>
                    <Text style={styles.text}>{capitalWeather.weather[0].main}</Text>
                </>}
            </View>
            <TouchableOpacity capital={item.capital} onPress={handlePress}>
                <Image style={styles.countryFlag} source={{uri: item.flag}} />
            </TouchableOpacity>

        </View>)
    }

    return (
        <FlatList style={styles.list} data={countries} renderItem={renderCountrie} keyExtractor={(item, index)=> index.toString()}/>
    )
}

const styles = StyleSheet.create({
    list:{
        margin: 10,
    },
    text:{
        marginHorizontal: 5,
    },
    dataCountrie:{
        marginVertical: 10,
    },
    dataCapital:{
       flexDirection: "row" ,
    },
    countryFlag:{
        height: 50,
        width: 100,
    },
})
