import React, { useEffect, useState } from 'react'
import { FlatList, Text, StyleSheet, Image } from 'react-native';

export function List() {
    const [countries, setCountries] = useState([])


    useEffect(()=>{

        const getCountries = async() =>{
            const res = await fetch("http://restcountries.eu/rest/v2/all");
            const data = await res.json();
            setCountries(data)

            
        }
        getCountries();
        
    },[])
    
    const renderCountrie = ({item}) =>{ 
        // countrie.map(

    // )
        return (<View style={styles.dataCountrie}>
        <Text>{item.name}</Text>
        <Text>{item.capital}</Text>
        <Image source={item.flag} />
        </View>)
    }



    console.log("countries",countries)
    return (
        <FlatList style={styles.list} data={countries} renderItem={renderCountrie} keyExtractor={(item, index)=> index.toString()}/>
    )
}

const styles = StyleSheet.create({
    list:{
        margin: 10,
    },
})
